function togglePersonalInfo(checkbox) {
    const serviceId = checkbox.id.replace('personalInfoCheck', '');
    const section = document.getElementById('personalInfoSection' + serviceId);

    if (checkbox.checked) {
        section.style.display = 'block';
        // Animation d'apparition
        section.style.opacity = '0';
        section.style.transform = 'translateY(-10px)';
        setTimeout(() => {
            section.style.transition = 'all 0.3s ease';
            section.style.opacity = '1';
            section.style.transform = 'translateY(0)';
        }, 10);
    } else {
        section.style.display = 'none';
        // Vider les champs si on décoche
        const inputs = section.querySelectorAll('input');
        inputs.forEach(input => input.value = '');
    }
}

// Validation côté client
document.addEventListener('DOMContentLoaded', function() {
    // Ajouter la validation pour tous les formulaires de ticket
    const forms = document.querySelectorAll('[id^="ticketForm"]');

    forms.forEach(form => {
        form.addEventListener('submit', function(e) {
            const serviceId = this.querySelector('input[name="service_id"]').value;
            const checkbox = document.getElementById('personalInfoCheck' + serviceId);

            if (checkbox && checkbox.checked) {
                const emailInput = this.querySelector('input[name="customer_email"]');
                const phoneInput = this.querySelector('input[name="customer_phone"]');

                // Validation email
                if (emailInput && emailInput.value) {
                    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
                    if (!emailRegex.test(emailInput.value)) {
                        e.preventDefault();
                        alert('Veuillez saisir une adresse email valide.');
                        emailInput.focus();
                        return;
                    }
                }

                // Validation téléphone (format ivoirien)
                if (phoneInput && phoneInput.value) {
                    const phoneValue = phoneInput.value.replace(/\s+/g, '');

                    // Formats acceptés pour la Côte d'Ivoire :
                    // +225 XX XX XX XX XX (international)
                    // 0X XX XX XX XX (national avec 0)
                    // XX XX XX XX XX (national sans 0, 10 chiffres)
                    const ivoirianPhoneRegex = /^(\+225\s?)?([0-9]{2}\s?[0-9]{2}\s?[0-9]{2}\s?[0-9]{2}\s?[0-9]{2}|0[1-9][0-9]{8})$/;

                    if (!ivoirianPhoneRegex.test(phoneValue)) {
                        e.preventDefault();
                        alert('Veuillez saisir un numéro de téléphone ivoirien valide.\nFormats acceptés :\n• +225 XX XX XX XX XX\n• 0X XX XX XX XX\n• XXXXXXXXXX (10 chiffres)');
                        phoneInput.focus();
                        return;
                    }
                }
            }
        });
    });
});

// Amélioration UX : formatage automatique du téléphone ivoirien
document.addEventListener('input', function(e) {
    if (e.target.name === 'customer_phone') {
        let value = e.target.value.replace(/\D/g, ''); // Supprimer tout sauf les chiffres

        // Gestion du format ivoirien
        if (value.startsWith('225')) {
            // Format international +225 XX XX XX XX XX
            value = '+225 ' + value.substring(3);
            if (value.length > 5) {
                value = value.replace(/(\+225\s)(\d{2})(\d{2})(\d{2})(\d{2})(\d{2})/, '$1$2 $3 $4 $5 $6');
            }
        } else if (value.startsWith('0') && value.length <= 10) {
            // Format national 0X XX XX XX XX
            if (value.length >= 3) {
                value = value.replace(/(\d{2})(\d{2})?(\d{2})?(\d{2})?(\d{2})?/, function(match, p1, p2, p3, p4, p5) {
                    let formatted = p1;
                    if (p2) formatted += ' ' + p2;
                    if (p3) formatted += ' ' + p3;
                    if (p4) formatted += ' ' + p4;
                    if (p5) formatted += ' ' + p5;
                    return formatted;
                });
            }
        } else if (value.length === 10 && !value.startsWith('0')) {
            // Format 10 chiffres sans 0 initial
            value = value.replace(/(\d{2})(\d{2})(\d{2})(\d{2})(\d{2})/, '$1 $2 $3 $4 $5');
        } else if (value.length > 10 && !value.startsWith('225')) {
            // Limiter à 10 chiffres pour les numéros nationaux
            value = value.substring(0, 10);
            if (value.startsWith('0')) {
                value = value.replace(/(\d{2})(\d{2})(\d{2})(\d{2})(\d{2})/, '$1 $2 $3 $4 $5');
            } else {
                value = value.replace(/(\d{2})(\d{2})(\d{2})(\d{2})(\d{2})/, '$1 $2 $3 $4 $5');
            }
        }

        // Mise à jour de la valeur si elle a changé
        if (e.target.value !== value && value.length <= 25) {
            const cursorPos = e.target.selectionStart;
            e.target.value = value;
            // Repositionner le curseur intelligemment
            const newCursorPos = Math.min(cursorPos + (value.length - e.target.value.length), value.length);
            e.target.setSelectionRange(newCursorPos, newCursorPos);
        }
    }
});

// Amélioration UX : capitalisation automatique du nom
document.addEventListener('input', function(e) {
    if (e.target.name === 'customer_name') {
        const words = e.target.value.toLowerCase().split(' ');
        const capitalizedWords = words.map(word => 
            word.charAt(0).toUpperCase() + word.slice(1)
        );
        const capitalizedValue = capitalizedWords.join(' ');

        if (e.target.value !== capitalizedValue) {
            const cursorPos = e.target.selectionStart;
            e.target.value = capitalizedValue;
            e.target.setSelectionRange(cursorPos, cursorPos);
        }
    }
});

