// Variables globales
let isLoading = false;
let csrfToken = null;

// Initialisation
document.addEventListener('DOMContentLoaded', function() {
    updateCurrentTime();
    startAutoRefresh();
    getCsrfToken();
});

// Récupération du token CSRF
function getCsrfToken() {
    const metaCsrf = document.querySelector('meta[name="csrf-token"]');
    if (metaCsrf) {
        csrfToken = metaCsrf.getAttribute('content');
    }
}

// Mise à jour de l'heure
function updateCurrentTime() {
    const now = new Date();
    const timeStr = now.toLocaleString('fr-FR', {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
    });
    const timeElement = document.getElementById('current-time');
    if (timeElement) {
        timeElement.textContent = timeStr;
    }
}

// Gestion du loading
function showLoading() {
    if (isLoading) return;
    isLoading = true;
    const overlay = document.getElementById('loading-overlay');
    if (overlay) {
        overlay.style.display = 'flex';
    }
}

function hideLoading() {
    isLoading = false;
    const overlay = document.getElementById('loading-overlay');
    if (overlay) {
        overlay.style.display = 'none';
    }
}

// Appel du prochain ticket - CORRIGÉ
// Appel du prochain ticket - VERSION CORRIGÉE POUR ODOO 17
function callNextTicket(button) {
    if (isLoading) return;

    const serviceId = button.getAttribute('data-service-id');
    if (!serviceId) {
        showNotification('error', 'ID du service manquant');
        return;
    }

    showLoading();
    button.disabled = true;

    // Utiliser l'API JSON avec fetch
    fetch('/queue/admin/call_next', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'X-Requested-With': 'XMLHttpRequest'
        },
        body: JSON.stringify({
            service_id: parseInt(serviceId),
            csrf_token: csrfToken
        })
    })
    .then(response => {
        if (!response.ok) {
            throw new Error(`HTTP ${response.status}`);
        }
        return response.json();
    })
    .then(data => {
        hideLoading();
        button.disabled = false;

        if (data.success) {
            showNotification('success', data.message || 'Prochain ticket appelé!');
            // Rafraîchir après un court délai
            setTimeout(() => refreshDashboard(), 1500);
        } else {
            showNotification('error', data.error || 'Erreur lors de l\'appel');
        }
    })
    .catch(error => {
        hideLoading();
        button.disabled = false;
        console.error('Erreur appel ticket:', error);
        showNotification('error', 'Erreur de connexion');
    });
}

// Fonctions manquantes - AJOUTER DANS LE SCRIPT
function refreshDashboard() {
    if (isLoading) return;
    showLoading();
    location.reload();
}

function startAutoRefresh() {
    setInterval(() => {
        updateCurrentTime();
    }, 60000);

    // Refresh complet toutes les 5 minutes
    setInterval(() => {
        if (!isLoading) {
            console.log('Auto-refresh du dashboard');
            location.reload();
        }
    }, 300000);
}

function updateCurrentTime() {
    const now = new Date();
    const timeStr = now.toLocaleString('fr-FR', {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
    });
    const timeElement = document.getElementById('current-time');
    if (timeElement) {
        timeElement.textContent = timeStr;
    }
}

// Voir les tickets du service - CORRIGÉ
function viewServiceTickets(button) {
    const serviceId = button.getAttribute('data-service-id');
    if (!serviceId) {
        showNotification('error', 'ID du service manquant');
        return;
    }
    window.open(`/queue/admin/service/${serviceId}/tickets`, '_blank');
}

