    setTimeout(() => {
        location.reload();
    }, 1000);
}

// Générer les références - CORRIGÉ
function generateReferences() {
    if (isLoading) return;

    if (!confirm('Générer les références manquantes pour tous les tickets ?')) {
        return;
    }

    showLoading();

    fetch('/queue/admin/generate_references', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/x-www-form-urlencoded',
            'X-Requested-With': 'XMLHttpRequest'
        },
        body: csrfToken ? `csrf_token=${csrfToken}` : ''
    })
    .then(response => {
        if (!response.ok) {
            throw new Error(`HTTP ${response.status}`);
        }
        return response.json();
    })
    .then(data => {
        hideLoading();
        if (data.success) {
            showNotification('success', data.message);
            setTimeout(() => location.reload(), 2000);
        } else {
            showNotification('error', data.error || 'Erreur lors de la génération');
        }
    })
    .catch(error => {
        hideLoading();
        console.error('Erreur génération références:', error);
        showNotification('error', 'Erreur de connexion');
    });
}

// Export des tickets - CORRIGÉ
function exportTickets() {
    if (isLoading) return;
    showLoading();
    showNotification('info', 'Préparation de l\'export...');

    try {
        const exportUrl = '/queue/export/tickets';
        const link = document.createElement('a');
        link.href = exportUrl;
        link.download = `tickets_${new Date().toISOString().split('T')[0]}.csv`;
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);

        setTimeout(() => {
            hideLoading();
            showNotification('success', 'Export téléchargé!');
        }, 2000);
    } catch (error) {
        hideLoading();
        showNotification('error', 'Erreur lors de l\'export');
    }
}

// Mode maintenance - CORRIGÉ
function maintenanceMode() {
    if (confirm('Accéder à la page de maintenance ?')) {
        window.location.href = '/queue/admin/references';
    }
}

// Actualiser le dashboard - CORRIGÉ
function refreshDashboard() {
    if (isLoading) return;
    showLoading();
    location.reload();
}

// Notifications améliorées
function showNotification(type, message) {
    // Supprimer les notifications existantes
    const existingNotifications = document.querySelectorAll('.alert-notification');
    existingNotifications.forEach(n => n.remove());

    // Créer la nouvelle notification
    const notification = document.createElement('div');
    const alertClass = type === 'success' ? 'alert-success' : 
                    type === 'error' ? 'alert-danger' : 
                    type === 'info' ? 'alert-info' : 'alert-warning';

    notification.className = `alert ${alertClass} alert-notification alert-dismissible fade show`;

    const icon = type === 'success' ? 'fas fa-check-circle' : 
            type === 'error' ? 'fas fa-exclamation-circle' :
            type === 'info' ? 'fas fa-info-circle' : 'fas fa-exclamation-triangle';

    notification.innerHTML = `

        ${message}

    `;

    document.body.appendChild(notification);

    // Auto-remove après 4 secondes
    setTimeout(() => {
        if (notification.parentElement) {
            notification.classList.remove('show');
            setTimeout(() => notification.remove(), 150);
        }
    }, 4000);
}

// Auto-refresh amélioré
function startAutoRefresh() {
    setInterval(() => {
        updateCurrentTime();
    }, 60000); // Mise à jour de l'heure chaque minute

    // Refresh complet toutes les 5 minutes
    setInterval(() => {
        if (!isLoading) {
            console.log('Auto-refresh du dashboard');
            location.reload();
        }
    }, 300000); // 5 minutes
}

