                // Générer références manquantes - CORRIGÉ
                function generateMissingReferences() {
                    if (isMaintenanceRunning) {
                        showMaintenanceNotification('warning', 'Une opération de maintenance est déjà en cours');
                        return;
                    }

                    if (!confirm('Générer les références manquantes pour tous les tickets ?\n\nCette opération peut prendre du temps selon le nombre de tickets.')) {
                        return;
                    }

                    isMaintenanceRunning = true;
                    showMaintenanceLoader('Génération des références en cours...');

                    fetch('/queue/admin/generate_references', {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/x-www-form-urlencoded',
                            'X-Requested-With': 'XMLHttpRequest'
                        },
                        body: csrfToken ? `csrf_token=${csrfToken}` : ''
                    })
                    .then(response => {
                        if (!response.ok) {
                            throw new Error(`HTTP ${response.status}: ${response.statusText}`);
                        }
                        return response.json();
                    })
                    .then(data => {
                        hideMaintenanceLoader();
                        isMaintenanceRunning = false;

                        if (data.success) {
                            showMaintenanceNotification('success', data.message || 'Références générées avec succès');
                            setTimeout(() => location.reload(), 2000);
                        } else {
                            showMaintenanceNotification('error', data.error || 'Erreur lors de la génération');
                        }
                    })
                    .catch(error => {
                        hideMaintenanceLoader();
                        isMaintenanceRunning = false;
                        console.error('Erreur génération références:', error);
                        showMaintenanceNotification('error', `Erreur: ${error.message}`);
                    });
                }

                // Nettoyer anciens tickets - CORRIGÉ
                function cleanupOldTickets() {
                    if (isMaintenanceRunning) {
                        showMaintenanceNotification('warning', 'Une opération de maintenance est déjà en cours');
                        return;
                    }

                    const daysToKeep = prompt('Nombre de jours à conserver pour les tickets annulés :', '30');
                    if (!daysToKeep || isNaN(daysToKeep) || parseInt(daysToKeep) < 1) {
                        showMaintenanceNotification('warning', 'Nombre de jours invalide');
                        return;
                    }

                    if (!confirm(`Supprimer les tickets annulés de plus de ${daysToKeep} jours ?\n\nCette action est irréversible.`)) {
                        return;
                    }

                    isMaintenanceRunning = true;
                    showMaintenanceLoader('Nettoyage des anciens tickets...');

                    fetch('/queue/maintenance', {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/x-www-form-urlencoded',
                            'X-Requested-With': 'XMLHttpRequest'
                        },
                        body: `operation=cleanup_old_tickets&days_to_keep=${daysToKeep}${csrfToken ? `&csrf_token=${csrfToken}` : ''}`
                    })
                    .then(response => {
                        if (!response.ok) {
                            throw new Error(`HTTP ${response.status}`);
                        }
                        return response.json();
                    })
                    .then(data => {
                        hideMaintenanceLoader();
                        isMaintenanceRunning = false;

                        if (data.success) {
                            showMaintenanceNotification('success', data.message || 'Nettoyage terminé');
                            setTimeout(() => location.reload(), 2000);
                        } else {
                            showMaintenanceNotification('error', data.error || 'Erreur lors du nettoyage');
                        }
                    })
                    .catch(error => {
                        hideMaintenanceLoader();
                        isMaintenanceRunning = false;
                        console.error('Erreur nettoyage:', error);
                        showMaintenanceNotification('error', `Erreur: ${error.message}`);
                    });
                }

                // Maintenance programmée - CORRIGÉ
                function scheduledMaintenance() {
                    if (isMaintenanceRunning) {
                        showMaintenanceNotification('warning', 'Une opération de maintenance est déjà en cours');
                        return;
                    }

                    if (!confirm('Effectuer la maintenance programmée complète ?\n\nCela inclut :\n- Génération des références manquantes\n- Vérification de l\'unicité\n- Nettoyage des données\n- Mise à jour des statistiques')) {
                        return;
                    }

                    isMaintenanceRunning = true;
                    showMaintenanceLoader('Maintenance programmée en cours...');

                    fetch('/queue/maintenance', {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/x-www-form-urlencoded',
                            'X-Requested-With': 'XMLHttpRequest'
                        },
                        body: `operation=scheduled_maintenance${csrfToken ? `&csrf_token=${csrfToken}` : ''}`
                    })
                    .then(response => {
                        if (!response.ok) {
                            throw new Error(`HTTP ${response.status}`);
                        }
                        return response.json();
                    })
                    .then(data => {
                        hideMaintenanceLoader();
                        isMaintenanceRunning = false;

                        if (data.success) {
                            showMaintenanceNotification('success', 'Maintenance programmée terminée avec succès!');
                            setTimeout(() => location.reload(), 3000);
                        } else {
                            showMaintenanceNotification('error', data.error || 'Erreur lors de la maintenance');
                        }
                    })
                    .catch(error => {
                        hideMaintenanceLoader();
                        isMaintenanceRunning = false;
                        console.error('Erreur maintenance:', error);
                        showMaintenanceNotification('error', `Erreur: ${error.message}`);
                    });
                }

                // Vérifier unicité des références - NOUVEAU
                function checkUniqueReferences() {
                    if (isMaintenanceRunning) {
                        showMaintenanceNotification('warning', 'Une opération est déjà en cours');
                        return;
                    }

                    isMaintenanceRunning = true;
                    showMaintenanceLoader('Vérification de l\'unicité...');

                    fetch('/queue/maintenance', {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/x-www-form-urlencoded',
                            'X-Requested-With': 'XMLHttpRequest'
                        },
                        body: `operation=check_uniqueness${csrfToken ? `&csrf_token=${csrfToken}` : ''}`
                    })
                    .then(response => response.json())
                    .then(data => {
                        hideMaintenanceLoader();
                        isMaintenanceRunning = false;

                        if (data.success && data.result) {
                            const result = data.result;
                            if (result.all_unique) {
                                showMaintenanceNotification('success', '✅ Toutes les références sont uniques!');
                            } else {
                                const duplicateCount = result.duplicates ? result.duplicates.length : 0;
                                showMaintenanceNotification('warning', `⚠️ ${duplicateCount} références dupliquées trouvées`);
                            }
                        } else {
                            showMaintenanceNotification('error', data.error || 'Erreur lors de la vérification');
                        }
                    })
                    .catch(error => {
                        hideMaintenanceLoader();
                        isMaintenanceRunning = false;
                        showMaintenanceNotification('error', `Erreur: ${error.message}`);
                    });
                }

