                // Affichage du loader de maintenance - AMÉLIORÉ
                function showMaintenanceLoader(message) {
                    // Supprimer le loader existant
                    const existingLoader = document.getElementById('maintenance-loader');
                    if (existingLoader) {
                        existingLoader.remove();
                    }

                    const loader = document.createElement('div');
                    loader.id = 'maintenance-loader';
                    loader.innerHTML = `






